//GLOBAL VARIABLES ==========

// Score, points generated by clicking on the crystals
var score;

// Create Random Number which will be the number to match
// Place random number value in panel 
var randomNumber;

var win = 0;
var loss = 0;

// FUNCTIONS =================================================

function reset() {
   // reset 
   score = 0;
   randomNumber = Math.floor(Math.random() * (120 - 19) + 1);
   emeraldNumber = Math.floor(Math.random() * (12 - 1) + 1);
   aquaNumber = Math.floor(Math.random() * (12 - 1) + 1);
   rubyNumber = Math.floor(Math.random() * (12 - 1) + 1);
   violetNumber = Math.floor(Math.random() * (12 - 1) + 1);

   // HTML
   $('#randomNumber').html(randomNumber);
   $('.score').html(score);
   start();

}

function start() {
   // reset
   // Create random image values
   score = 0;
   randomNumber = Math.floor(Math.random() * (120 - 19) + 1);
   console.log(randomNumber);
   emeraldNumber = Math.floor(Math.random() * (12 - 1) + 1);
   console.log(emeraldNumber);
   aquaNumber = Math.floor(Math.random() * (12 - 1) + 1);
   console.log(aquaNumber);
   rubyNumber = Math.floor(Math.random() * (12 - 1) + 1);
   console.log(rubyNumber);
   violetNumber = Math.floor(Math.random() * (12 - 1) + 1);
   console.log(violetNumber);

   // HTML

   $('#randomNumber').html(randomNumber);
   $('#score').html(score);

   // Number for images
   $('.emerald').attr("value", emeraldNumber);
   var test = $('.emerald').attr("value");

   $('.aqua').attr('value', aquaNumber);
   var test = $('.aqua').attr('value');

   $('.ruby').attr('value', rubyNumber);
   var test = $('ruby').attr('value');

   $('.violet').attr('value', violetNumber);
   var test = $('violet').attr('value');

   // when clicked saves to final score
   $('.box').off().on('click', function () {
      var test1 = $(this).attr('value');
      // adds to score  
      score = parseInt(score) + parseInt(test1);
      $('#score').html(score);
      if (score === randomNumber) {
         alert('you won');
         win++;
         $('#win').html(win);
         reset();
      }
      if (score > randomNumber) {
         alert('you lose');
         loss++;
         $('#loss').html(loss);
         reset();
      }
   });
};
start();